import {Layout} from '~/components/Layout'
import React, {useEffect, useState} from 'react'
import apiProduct from '~/api/product'
import {Table, Button, Form, Input, Card, DatePicker, Select} from 'antd'
import moment from 'moment'
import apiAccounting from '~/api/accounting'
import apiBusiness from '~/api/business'
import {useCookies} from 'react-cookie'
const pageParams = {
  pageNum: 1,
  pageSize: 10,
}
const {RangePicker} = DatePicker

const getCurrentMonthFirst = () => {
  var date = new Date()
  date.setDate(1)
  return date
}

const getData = async (values = {}) => {
  const {time, time2} = values
  if (Array.isArray(time)) {
    values = {
      ...values,
      startTime: moment(time[0]).format('YYYY-MM-DD'),
      endTime: moment(time[1]).format('YYYY-MM-DD'),
    }
  } else if (time === undefined) {
    values = {
      ...values,
      startTime: moment(getCurrentMonthFirst(), 'YYYY-MM-DD').format(
        'YYYY-MM-DD',
      ),
      endTime: moment(
        new Date(new Date().setDate(new Date().getDate())),
      ).format('YYYY-MM-DD'),
    }
  } else if (time === null) {
    values = {
      ...values,
      startTime: null,
      endTime: null,
    }
  }
  values = {
    ...values,
    startTime2: moment(time2[0]).format('YYYY-MM-DD'),
    endTime2: moment(time2[1]).format('YYYY-MM-DD'),
  }
  delete values.time
  delete values.time2
  try {
    let {
      data: {code, data},
    } = await apiProduct.takeBackReports({
      ...pageParams,
      ...values,
    })
    if (code == 0) {
      return data
    }
    return {
      total: 0,
      list: [],
    }
  } catch (e) {
    return {
      total: 0,
      list: [],
    }
  }
}

const breadcrumbs = [{text: 'BI报表'}, {text: '业务台账'}, {text: '收回报表'}]

function body(props) {
  const [cookies] = useCookies(['tenantId'])
  const [data, setData] = useState(props.data)
  const [form] = Form.useForm()
  const [searchParams, setSearchParams] = useState({})
  const [products, setProducts] = useState([])

  useEffect(() => {
    getBranch4search()
  }, [])

  const getBranch4search = async () => {
    let {
      data: {code, data},
    } = await apiBusiness.getBranch4other()
    if (code == 0) {
      setProducts(data || [])
    } else {
      setProducts([])
    }
  }

  const onSearch = (value) => {
    pageParams.pageNum = 1
    updateList({...value, ...pageParams})
    setSearchParams({...value, ...pageParams})
  }
  const onResetForm = () => {
    form.resetFields()
    onSearch()
  }

  const onChangePage = (pageNum = pageParams.pageNum) => {
    pageParams.pageNum = pageNum
    updateList({...searchParams, ...pageParams})
  }

  const updateList = async (params) => {
    let data = await getData({...params})
    console.log(data)
    setData(data)
  }

  const columns = [
    {
      title: '部门',
      dataIndex: 'orgName',
      align: 'left',
      fixed: 'left',
    },
    {
      title: '借款人名称',
