import {get, post, request} from '~/utils/fetch'
export default {
  get_roles: (params) => {
    return get(`roles`, params)
  },
  get_one_role: (id) => {
    return request(`roles/${id}`, 'get', {})
  },
  handle_role: (method, params) => {
    return request(`roles`, method, params)
  },
  delete_role: (id) => {
    return request(`roles/${id}`, 'post')
  },
  get_resource: (method, params) => {
    return request(`roles/roleConfig`, method, params)
  },
  get_users: (params) => {
    return request(`users`, 'get', params)
  },
  handle_account: (method, params) => {
    return request(`users`, method, params)
  },
  delete_account: (id) => {
    return request(`users/${id}`, 'post')
  },
  set_account_status: (params) => {
    return request(`users/disabled`, 'post', params)
  },
  put_resource: (params) => {
    return request(`roles/roleConfig`, 'post', params)
  },
  get_core_index: (params) => {
    return request(`coreIndex/query`, 'get', params)
  },
  getRoleList: (params) => {
    return post(`newfc/queryRole`, params)
  },
  createRole: (params) => {
    return post(`newfc/addRole`, params)
  },
  editRole: (params) => {
    return post(`newfc/editRole`, params)
  },
  deleteRole: (id) => {
    return post(`newfc/deleteRole`, {id})
  },
  // 部门管理
  /**
   * @param params
   * name: 部门名称
   * parentId: 父级部门id
   * @returns {*}
   */
  getDepartmentList: (params) => {
    return post(`newfc/queryDepartment`, params)
  },
  editDepartment: (params) => {
    if (params.id) {
      return post(`newfc/editDepartment`, params)
    } else {
      return post(`newfc/addDepartment`, params)
    }
  },
  deleteDepartment: (id) => {
    return post(`newfc/deleteDepartment`, {id})
  },
  getVisibleDepart: () => {
    return post(`newfc/queryCurrentVisibleDepartments`)
  },
  getAccount: (params) => {
    return post(`newfc/queryAccount`, params)
  },
  getDepartmentAccount: (departmentId) => {
    return post(`newfc/queryDepartmentAccount`, {departmentId})
  },
  changeAccountStatus: (params) => {
    return post(`newfc/changeAccountStatus`, params)
  },
  deleteAccount: (id) => {
    return post(`newfc/deleteAccount`, {id})
  },
  editAccount: (params) => {
    if (params.id) {
      return post(`newfc/editAccount`, params)
    } else {
      return post(`newfc/addAccount`, params)
    }
  },
}
