import {Layout} from '~/components/Layout'
import React, {useEffect, useState} from 'react'
import apiProduct from '~/api/product'
import {Table, Button, Form, Input, Card, DatePicker, Select} from 'antd'
import moment from 'moment'
import apiAccounting from '~/api/accounting'
import {useCookies} from 'react-cookie'
const pageParams = {
  pageNum: 1,
  pageSize: 10,
}
const {RangePicker} = DatePicker

const getCurrentMonthFirst = () => {
  var date = new Date()
  date.setDate(1)
  return date
}

const getData = async (values = {}) => {
  const {releaseDate} = values
  if (Array.isArray(releaseDate)) {
    values = {
      ...values,
      startTime: moment(releaseDate[0]).format('YYYY-MM-DD'),
      endTime: moment(releaseDate[1]).format('YYYY-MM-DD'),
    }
  } else if (releaseDate === undefined) {
    values = {
      ...values,
      startTime: moment(getCurrentMonthFirst(), 'YYYY-MM-DD').format(
        'YYYY-MM-DD',
      ),
      endTime: moment(
        new Date(new Date().setDate(new Date().getDate())),
      ).format('YYYY-MM-DD'),
    }
  } else if (releaseDate === null) {
    values = {
      ...values,
      startTime: null,
      endTime: null,
    }
  }
  delete values.releaseDate
  try {
    let {
      data: {code, data},
    } = await apiProduct.getMarketing({
      ...pageParams,
      ...values,
    })
    if (code == 0) {
      return data
    }
    return {
      total: 0,
      list: [],
    }
  } catch (e) {
    return {
      total: 0,
      list: [],
    }
  }
}

const breadcrumbs = [{text: 'BI报表'}, {text: '业务台账'}, {text: '营销报表'}]

function body(props) {
  const [cookies] = useCookies(['tenantId'])
  const [data, setData] = useState(props.data)
  const [form] = Form.useForm()
  const [searchParams, setSearchParams] = useState({})

  useEffect(() => {}, [])

  const onSearch = (value) => {
    pageParams.pageNum = 1
    updateList({...value, ...pageParams})
    setSearchParams({...value, ...pageParams})
  }
  const onResetForm = () => {
    form.resetFields()
    onSearch()
  }

  const onChangePage = (pageNum = pageParams.pageNum) => {
    pageParams.pageNum = pageNum
    updateList({...searchParams, ...pageParams})
  }

  const updateList = async (params) => {
    let data = await getData({...params})
    setData(data)
  }

  const columns = [
    {
      title: '发放日期',
      dataIndex: 'releaseDate',
      align: 'left',
      fixed: 'left',
    },
    {
      title: '客户经理手机号',
      dataIndex: 'managerMobile',
      align: 'left',
    },
    {
      title: '客户经理姓名',
      dataIndex: 'managerName',
      align: 'left',
    },
    {
      title: '营销部门',
      dataIndex: 'orgName',
      align: 'left',
    },
    {
      title: '客户姓名',
      dataIndex: 'userName',
      align: 'left',
    },
    {
      title: '客户证件号码',
      dataIndex: 'idCard',
      align: 'left',
    },
    {
      title: '客户手机号',
      dataIndex: 'mobile',
      align: 'left',
    },
    {
      title: '客户联系地址',
      dataIndex: 'address',
      align: 'left',
    },
    {
      title: '行业',
      dataIndex: 'industry',
      align: 'left',
    },
    {
      title: '是否首贷客户',
      dataIndex: 'isSd',
      align: 'left',
    },
    {
      title: '是否本行新增客户',
      dataIndex: 'isNew',
      align: 'left',
    },
    {
      title: '是否本行新增存量客户',
      dataIndex: 'isNewC',
      align: 'left',
    },
    {
      title: '贷款品种',
      dataIndex: 'productType',
      align: 'left',
    },
    {
      title: '贷款金额',
      dataIndex: 'loanAmount',
      align: 'left',
    },
    {
      title: '贷款余额',
      dataIndex: 'blance',
      align: 'left',
    },
    {
      title: '贷款利率',
      dataIndex: 'interest',
      align: 'left',
    },
    {
      title: '贷款期限',
      dataIndex: 'term',
      align: 'left',
    },
    {
      title: '到期日期',
      dataIndex: 'dueDate',
      align: 'left',
    },
    {
      title: '本月应收利息',
      dataIndex: 'interestReceivable',
      align: 'left',
    },
    {
      title: '本月实收利息',
      dataIndex: 'interestReceipts',
    },
    {
      title: '收息率',
      dataIndex: 'interestRate',
      align: 'left',
    },
    {
      title: '逾期本金',
      dataIndex: 'overduePrincipal',
      align: 'left',
    },
    {
      title: '逾期利息',
      dataIndex: 'overdueInterest',
      align: 'left',
    },
    {
      title: '逾期次数',
      dataIndex: 'overdueFrequency',
      align: 'left',
    },
  ]

  const paginationConfig = {
    total: data.total,
    current: pageParams.pageNum,
    showSizeChanger: false,
    showQuickJumper: true,
    defaultPageSize: pageParams.pageSize,
    showTotal: (total) => `共 ${total} 条`,
    onChange: onChangePage,
  }
  // const disabledDate = (current) => {
  //   return current > new Date()
  // }
  const exportExcel = async () => {
    const values = form.getFieldValue()
    let postData = {}
    try {
      if (Array.isArray(values.releaseDate)) {
        postData = {
          startTime: moment(values.releaseDate[0]).format('YYYY-MM-DD'),
          endTime: moment(values.releaseDate[1]).format('YYYY-MM-DD'),
        }
      }
      delete values.releaseDate
      const {
        data: {data, code},
      } = await apiAccounting.fetch_marketing_export({
        ...values,
        ...postData,
      })
      if (code == 0) {
        window.open(data)
      }
    } catch (err) {
      console.log(err)
    }
  }
  return (
    <Layout breadcrumbs={breadcrumbs}>
      <Card style={{marginBottom: 30}}>
        <Form
          form={form}
          name="search"
          onFinish={onSearch}
          layout="inline"
          initialValues={{
            releaseDate: [
              moment(getCurrentMonthFirst(), 'YYYY-MM-DD'),
              moment(
                new Date(new Date().setDate(new Date().getDate())),
                'YYYY-MM-DD',
              ),
            ],
            productId: null,
          }}
        >
          <Form.Item label="发放日期" name="releaseDate">
            <RangePicker />
          </Form.Item>
          <Form.Item label="客户经理手机号" name="managerMobile">
            <Input placeholder="请输入" />
          </Form.Item>
          <Form.Item label="客户经理姓名" name="managerName">
            <Input placeholder="请输入" />
          </Form.Item>
          <Form.Item label="营销部门" name="orgName">
            <Select style={{width: '130px'}} defaultValue="">
              <Select.Option value="">全部</Select.Option>
            </Select>
          </Form.Item>
          <Form.Item label="客户姓名" name="userName">
            <Input placeholder="请输入" />
          </Form.Item>
          <Form.Item label="客户证件号码" name="idCard">
            <Input placeholder="请输入" />
          </Form.Item>
          <Form.Item label="客户手机号" name="mobile">
            <Input placeholder="请输入" />
          </Form.Item>
          <Form.Item>
            <Button type="primary" htmlType="submit">
              查询
            </Button>
          </Form.Item>
          <Form.Item>
            <Button type="default" onClick={onResetForm}>
              重置
            </Button>
          </Form.Item>
          <Button
            style={{marginRight: 15}}
            onClick={exportExcel}
            type="primary"
          >
            导出
          </Button>
        </Form>
      </Card>
      <Table
        bordered
        rowKey={(v, i) => i}
        align="center"
        scroll={{x: 'max-content'}}
        pagination={paginationConfig}
        columns={columns}
        dataSource={data.list || []}
      />
    </Layout>
  )
}

body.getInitialProps = async (ctx) => {
  try {
    let data = await getData()
    return {data: data}
  } catch (error) {}
}

export default body
