import React, {useEffect, useState, useRef} from 'react'
import {Input, Button, Form, Select, DatePicker, Space} from 'antd'
import moment from 'moment'

const {RangePicker} = DatePicker

const APPLYSOURCE = {
  '1': '个人App android',
  '2': '个人App ios',
  '3': '小程序',
  '4': '客户经理App ios',
  '5': '客户经理App android',
}

const SearchForm = ({onSearch, productList, applyStatusList}) => {
  const [form] = Form.useForm()

  useEffect(() => {
    form.setFieldsValue({
      time: [moment(), moment()],
    })
    search()
  }, [])

  const getFields = () => {
    const children = [
      <Form.Item label="客户名称" name="realName">
        <Input placeholder="请输入" />
      </Form.Item>,
      <Form.Item label="手机号码" name="keyword">
        <Input placeholder="请输入" />
      </Form.Item>,
      <Form.Item label="产品名称" name="productId">
        <Select style={{width: '160px'}}>
          <Select.Option value="">全部</Select.Option>
          {productList.map((v, i) => (
            <Select.Option key={i} value={v.id}>
              {v.name}
            </Select.Option>
          ))}
        </Select>
      </Form.Item>,
      <Form.Item name="status" label="用信状态">
        <Select style={{width: '130px'}}>
          <Select.Option value="">全部</Select.Option>
          <Select.Option value={22}>用信失败</Select.Option>
          <Select.Option value={23}>用信成功</Select.Option>
        </Select>
      </Form.Item>,
      <Form.Item label="用信日期" name="time">
        <RangePicker />
      </Form.Item>,
    ]

    return children
  }

  const onReset = () => {
    form.resetFields()
    search()
  }

  const search = () => {
    const {time, ...params} = form.getFieldsValue()

    if (Array.isArray(time)) {
      params.beginCreateTime = moment(time[0]).format('YYYY-MM-DD')
      params.endCreateTime = moment(time[1]).format('YYYY-MM-DD')
    }
    onSearch(params)
  }

  return (
    <Form
      form={form}
      onFinish={(values) =>
        onSearch({
          ...values,
        })
      }
      layout="inline"
      className="searchForm"
      initialValues={{
        productId: '',
        checkProgress: '',
        status: '',
        applySource: '',
      }}
    >
      {getFields()}
      <Form.Item>
        <Space>
          <Button type="primary" onClick={search}>
            查询
          </Button>

          <Button onClick={onReset}>重置</Button>
          {/*<Button type="primary">*/}
          {/*  人工进件*/}
          {/*</Button>*/}
        </Space>
      </Form.Item>
    </Form>
  )
}

export default SearchForm
