import {Layout} from '~/components/Layout'
import React, {useEffect, useState} from 'react'
import apiProduct from '~/api/product'
import {Table, Button, Form, Input, Card, DatePicker, Select} from 'antd'
import moment from 'moment'
import apiAccounting from '~/api/accounting'
import apiBusiness from '~/api/business'
import {useCookies} from 'react-cookie'
const pageParams = {
  pageNum: 1,
  pageSize: 10,
}
const {RangePicker} = DatePicker

const getCurrentMonthFirst = () => {
  var date = new Date()
  date.setDate(1)
  return date
}
const getData = async (values = {}) => {
  const {time, time2} = values
  if (Array.isArray(time)) {
    values = {
      ...values,
      startTime: moment(time[0]).format('YYYY-MM-DD'),
      endTime: moment(time[1]).format('YYYY-MM-DD'),
    }
  }
  if (Array.isArray(time2)) {
    values = {
      ...values,
      startGrantTime: moment(time2[0]).format('YYYY-MM-DD'),
      endGrantTime: moment(time2[1]).format('YYYY-MM-DD'),
    }
  } else if (time2 === undefined) {
    values = {
      ...values,
      startGrantTime: moment(new Date()).format('YYYY-MM-DD'),
      endGrantTime: moment(new Date()).format('YYYY-MM-DD'),
    }
  }
  delete values.time
  delete values.time2
  console.log({...pageParams, ...values})
  try {
    let {
      data: {code, data},
    } = await apiProduct.getRecovery({
      ...pageParams,
      ...values,
    })
    if (code == 0) {
      return data
    }
    return {
      total: 0,
      list: [],
    }
  } catch (e) {
    return {
      total: 0,
      list: [],
    }
  }
}

const breadcrumbs = [{text: 'BI报表'}, {text: '业务台账'}, {text: '收回报表'}]

function body(props) {
  const [cookies] = useCookies(['tenantId'])
  const [data, setData] = useState(props.data)
  const [form] = Form.useForm()
  const [searchParams, setSearchParams] = useState({})
  const [products, setProducts] = useState([])

  useEffect(() => {
    getBranch4search()
  }, [])

  const onSearch = (value) => {
    pageParams.pageNum = 1
    updateList({...value, ...pageParams})
    setSearchParams({...value, ...pageParams})
  }
  const onResetForm = () => {
    form.resetFields()
    onSearch()
  }

  const getBranch4search = async () => {
    let {
      data: {code, data},
    } = await apiBusiness.getBranch4other()
    if (code == 0) {
      setProducts(data || [])
    } else {
      setProducts([])
    }
  }

  const onChangePage = (pageNum = pageParams.pageNum) => {
    pageParams.pageNum = pageNum
    updateList({...searchParams, ...pageParams})
  }

  const updateList = async (params) => {
    let data = await getData({...params})
    setData(data)
  }

  const columns = [
    {
      title: '部门',
      dataIndex: 'orgName',
      fixed: 'left',
    },
    {
      title: '借款人名称',
      dataIndex: 'userName',
    },
    {
      title: '借款人性质',
      dataIndex: 'natureofBorrower',
    },
    {
      title: '客户经理手机号',
      dataIndex: 'managerMobile',
    },
    {
      title: '客户经理',
      dataIndex: 'managerName',
    },
    {
      title: '借据号',
      dataIndex: 'orderNum',
    },
    {
      title: '是否广东省范围内贷款',
      dataIndex: 'isGD',
    },
    {
      title: '是否梅州市范围内贷款',
      dataIndex: 'isMZ',
    },
    {
      title: '是否农户',
      dataIndex: 'whetherFarmers',
    },
    {
      title: '涉农品种',
      dataIndex: 'agricultureVarieties',
    },
    {
      title: '涉农区域',
      dataIndex: 'agricultureRelatedArea',
    },
    {
      title: '结息方式',
      dataIndex: 'rementpalyType',
    },
    {
      title: '贷款种类',
      dataIndex: 'productType',
    },
    {
      title: '贷款投向一级',
      dataIndex: 'loanClassification1',
    },
    {
      title: '贷款投向二级',
      dataIndex: 'loanClassification2',
    },
    {
      title: '贷款投向三级',
      dataIndex: 'loanClassification3',
    },
    {
      title: '贷款投向四级',
      dataIndex: 'loanClassification4',
    },
    {
      title: '行业分类一级',
      dataIndex: 'industryClassification1',
    },
    {
      title: '行业分类二级',
      dataIndex: 'industryClassification2',
    },
    {
      title: '行业分类三级',
      dataIndex: 'industryClassification3',
    },
    {
      title: '行业分类四级',
      dataIndex: 'industryClassification4',
    },
    {
      title: '担保方式',
      dataIndex: 'guaranteeMode',
    },
    {
      title: '担保情况',
      dataIndex: 'guaranteeSituation',
    },
    {
      title: '借款期限',
      children: [
        {
          title: '借期',
          dataIndex: 'loanPeriod',
        },
        {
          title: '到期',
