import {Input, Button, Form, Select} from 'antd'
import React, {useEffect, useState} from 'react'
import api from '~/api/creditScore'
import {useCookies} from 'react-cookie'

const SearchForm = ({onQuery, fetchList, values, gradeList}) => {
  useEffect(() => {
    fetchItem()
  }, [])
  const [form] = Form.useForm()
  const [applyStatusList, setApplyStatusList] = useState([])
  const [productList, setProductList] = useState([])
  const [cookies] = useCookies(['tenantId'])

  const fetchItem = async () => {
    try {
      const {
        data: {data, code},
      } = await api.fetch_base_information({
        tenantId: cookies.tenantId,
        ...values,
      })
      if (code == 0) {
        setProductList(data)
        setApplyStatusList(data[0].subArea)
        form.setFieldsValue({
          streetCode: '',
          villageCode: '',
        })
      }
    } catch (err) {
      console.log(err)
    }
  }
  const codeChange = (val) => {
    if (!val) {
      form.setFieldsValue({
        villageCode: '',
      })
      return false
    }
    let arr = []
    productList.map((item) => {
      if (item['code'] === val) {
        arr = item.subArea
      }
    })
    setApplyStatusList(arr)
    form.setFieldsValue({
      villageCode: arr[0].code,
    })
  }

  const getFields = () => {
    const children = [
      <Form.Item label="村/社区名称:" name="name">
        <Input style={{width: 150}} placeholder="请输入村/社区名称" />
      </Form.Item>,
      <Form.Item
        label="所属街道/乡镇:"
        name="streetCode"
        style={{marginLeft: 50}}
      >
        <Select style={{width: 130}}>
          <Select.Option value="">全部</Select.Option>
          {productList.map((v, i) => {
            return (
              <Select.Option key={i} value={v.code}>
                {v.name}
              </Select.Option>
            )
          })}
        </Select>
      </Form.Item>,
      <Form.Item label="信用评级" name="creditLevel" style={{marginLeft: 50}}>
        <Select style={{width: 130}}>
          <Select.Option value="">全部</Select.Option>
          {gradeList.map((v, i) => {
            return (
              <Select.Option value={v.level} key={i}>
                {v.desc}
              </Select.Option>
            )
          })}
        </Select>
      </Form.Item>,
    ]

    return children
  }

  return (
    <Form
      form={form}
      onFinish={(values) =>
        onQuery({
          ...values,
        })
      }
      layout="inline"
      className="searchForm"
      initialValues={{
        creditLevel: '',
        streetCode: '',
      }}
    >
      {getFields()}
      <Button type="primary" style={{marginLeft: 80}} htmlType="submit">
        查询
      </Button>
    </Form>
  )
}

export default SearchForm
