import Router from 'next/router'
import React, {useEffect, useState} from 'react'
import {Table, Button} from 'antd'

const TableList = ({list, setList, totalData, pageParams, onPage}) => {
  const [pageSize, setPageSize] = useState(pageParams.pageSize)
  const columns = [
    {
      title: '客户名',
      dataIndex: 'name',
      key: 'name',
      width: 120,
    },
    {
      title: '身份证号',
      dataIndex: 'idCard',
      key: 'idCard',
      width: 130,
    },
    {
      title: '手机号',
      dataIndex: 'mobilePhone',
      key: 'mobilePhone',
      width: 130,
    },
    {
      title: '信用分',
      dataIndex: 'creditScore',
      key: 'creditScore',
      width: 130,
    },
    {
      title: '信用评级',
      dataIndex: 'creditLevel',
      key: 'creditLevel',
      width: 130,
    },
    {
      title: '所属街道/乡镇',
      dataIndex: 'streetName',
      key: 'streetName',
      width: 200,
    },
    {
      title: '所属村/社区',
      dataIndex: 'villageName',
      key: 'villageName',
      width: 200,
    },
    {
      title: '更新时间',
      dataIndex: 'updateTimeStr',
      key: 'updateTimeStr',
      width: 150,
    },
    {
      title: '操作',
      dataIndex: 'cz',
      key: 'cz',
      width: 80,
      render: (text, record, index) => {
        return (
          <>
            <Button type="link" onClick={() => onEdit(record)}>
              查看
            </Button>
          </>
        )
      },
    },
  ]
  const onEdit = (record) => {
    let url = `/vitalization/creditScore/personal/detail?id=${record.id}&name=${record.name}`
    Router.push(url)
  }
  const onShowSizeChange = (current, pageSize) => {
    console.log(current, pageSize)
    pageParams.pageSize = pageSize
    setPageSize(pageSize)
  }
  const pagination = {
    defaultCurrent: 1,
    total: totalData.total,
    pageSize: pageSize,
    showQuickJumper: true,
    showSizeChanger: true,
    onShowSizeChange: onShowSizeChange,
    current: pageParams.pageNum,
    showTotal: (total) => {
      return `共 ${total} 条记录`
    },
    onChange: (pageNumber) => {
      pageParams.pageNum = pageNumber
      onPage()
    },
  }
  const onEditStatus = (record) => {
    record.status = !record.status
    setList([...list])
  }

  return (
    <Table
      rowKey={(record) => record.id}
      dataSource={list}
      columns={columns}
      pagination={pagination}
    />
  )
}

export default TableList
