import {Input, Button, Form, Select} from 'antd'
import React, {useEffect, useState, useRef} from 'react'
import api from '~/api/creditScore'
import {useCookies} from 'react-cookie'

const SearchForm = ({onSearch, fetchList, values, gradeList}) => {
  useEffect(() => {
    fetchItem()
  }, [])
  const [form] = Form.useForm()
  const [applyStatusList, setApplyStatusList] = useState([])
  const [productList, setProductList] = useState([])
  const [cookies] = useCookies(['tenantId'])
  const fetchItem = async () => {
    try {
      const {
        data: {data, code},
      } = await api.fetch_base_information({
        tenantId: cookies.tenantId,
        ...values,
      })
      if (code == 0) {
        setProductList(data)
        // setApplyStatusList(data[0].subArea)
        form.setFieldsValue({
          streetCode: '',
          villageCode: '',
        })
      }
    } catch (err) {
      console.log(err)
    }
  }
  const codeChange = (val) => {
    if (!val) {
      form.setFieldsValue({
        villageCode: '',
      })
      setApplyStatusList([])
      return false
    }
    let arr = []
    productList.map((item) => {
      if (item['code'] === val) {
        arr = item.subArea
      } else {
        console.log(item, val)
      }
    })
    setApplyStatusList(arr)
    form.setFieldsValue({
      villageCode: arr[0].code,
    })
  }
  const getFields = () => {
    const children = [
      <Form.Item label="客户名:" name="name">
        <Input placeholder="请输入客户名" style={{width: 130}} />
      </Form.Item>,
      <Form.Item label="身份证号:" name="idCard">
        <Input placeholder="请输入身份证号" />
      </Form.Item>,
      <Form.Item label="手机号:" name="mobilePhone">
        <Input placeholder="请输入手机号" style={{width: 130}} maxLength={11} />
      </Form.Item>,
      <Form.Item label="信用评级:" name="creditLevel">
        <Select style={{width: 130}}>
          <Select.Option value="">全部</Select.Option>
          {gradeList.map((v, i) => {
            return (
              <Select.Option value={v.level} key={i}>
                {v.desc}
              </Select.Option>
            )
          })}
        </Select>
      </Form.Item>,
      <Form.Item label="地区:" name="streetCode">
        <Select
          style={{width: 130, marginRight: 5}}
          onChange={(val) => codeChange(val)}
        >
          <Select.Option value="">街道/乡镇</Select.Option>
          {productList.map((v, i) => {
            return (
              <Select.Option key={i} value={v.code}>
                {v.name}
              </Select.Option>
            )
          })}
        </Select>
      </Form.Item>,
      <Form.Item name="villageCode">
        <Select style={{width: 130}}>
          <Select.Option value="">村/社区</Select.Option>
          {applyStatusList.map((v, i) => {
            return (
              <Select.Option key={i} value={v.code}>
                {v.name}
              </Select.Option>
            )
          })}
        </Select>
      </Form.Item>,
    ]

    return children
  }

  return (
    <Form
      form={form}
      onFinish={(values) =>
        onSearch({
          ...values,
        })
      }
      layout="inline"
      className="searchForm"
      initialValues={{
        creditLevel: '',
        streetCode: '',
        villageCode: '',
      }}
    >
      {getFields()}
      <Button type="primary" style={{marginLeft: 10}} htmlType="submit">
        查询
      </Button>
    </Form>
  )
}

export default SearchForm
