import {useEffect, useState, useRef} from 'react'
import {Layout} from '~/components/Layout'
import {Button, DatePicker, Form, Select, Table, Tabs, Space} from 'antd'
import nextId from 'react-id-generator'
import moment from 'moment'
import {biFetch} from '~/utils/fetch'
import {CSVLink} from 'react-csv'

const {RangePicker} = DatePicker
const {Option} = Select
const {TabPane} = Tabs

const breadcrumbs = [{text: 'BI报表'}, {text: '贷后分析'}, {text: '催收报表'}]

const tabs = ['到期金额', '到期笔数']
const urls = ['/coll/stat/amount', '/coll/stat/count']

const columns = [
  [
    {
      title: '到期日期',
      dataIndex: 'dueDate',
      align: 'left',
      width: 150,
      fixed: 'left',
    },
    {
      title: '渠道',
      dataIndex: 'srcChannel',
      align: 'left',
      width: 100,
      fixed: 'left',
    },
    {
      title: '到期本金',
      dataIndex: 'dueCapitalAmt',
      align: 'left',
      width: 100,
      fixed: 'left',
    },
    {
      title: '入催本金',
      dataIndex: 'collCapitalAmt',
      align: 'left',
      width: 100,
      fixed: 'left',
    },
    {
      title: '入催率',
      dataIndex: 'dueCollRate',
      align: 'left',
      width: 100,
      fixed: 'left',
    },
    {
      title: '催回率',
      dataIndex: 'collBackRate',
      align: 'left',
      width: 100,
      fixed: 'left',
    },
    ...[1, 3, 10, 30, 60, 90].map((v) => ({
      title: `${v}日内催回本金`,
      dataIndex: `collBackAmtDay${v}`,
      align: 'left',
      width: 150,
    })),
    ...[1, 3, 10, 30, 60, 90].map((v) => ({
      title: `${v}日催回率`,
      dataIndex: `collBackRateDay${v}`,
      align: 'left',
      width: 120,
    })),
  ],
  [
    {
      title: '到期日期',
      dataIndex: 'dueDate',
      align: 'left',
      width: 100,
      fixed: 'left',
    },
    {
      title: '渠道',
      dataIndex: 'srcChannel',
      align: 'left',
      width: 100,
      fixed: 'left',
    },
    {
      title: '到期笔数',
      dataIndex: 'dueCnt',
      align: 'left',
      width: 100,
      fixed: 'left',
    },
    {
      title: '入催笔数',
      dataIndex: 'collCnt',
      align: 'left',
      width: 100,
      fixed: 'left',
    },
    {
      title: '入催率',
      dataIndex: 'dueCollRate',
      align: 'left',
      width: 100,
      fixed: 'left',
    },
    {
      title: '催回率',
      dataIndex: 'collBackRate',
      align: 'left',
      width: 100,
      fixed: 'left',
    },
    ...[1, 3, 10, 30, 60, 90].map((v) => ({
      title: `${v}日内催回笔数`,
      dataIndex: `collBackCntDay${v}`,
      align: 'left',
      width: 150,
    })),
    ...[1, 3, 10, 30, 60, 90].map((v) => ({
      title: `${v}日内催回率`,
      dataIndex: `collBackRateDay${v}`,
      align: 'left',
      width: 120,
    })),
  ],
]

const pages = [
  {
    pageNo: 1,
    pageSize: 20,
  },
  {
    pageNo: 1,
    pageSize: 20,
  },
]

const startDate = moment().subtract(7, 'days').format('YYYY-MM-DD')
const endDate = moment().subtract(1, 'days').format('YYYY-MM-DD')

const fetchList = async (url, params) => {
  for (const [key, value] of Object.entries(params)) {
    if (!value) {
      delete params[key]
    }
  }
  const startDate = Array.isArray(params.date)
    ? moment(params.date[0]).format('YYYY-MM-DD')
    : undefined
  const endDate = Array.isArray(params.date)
    ? moment(params.date[1]).format('YYYY-MM-DD')
    : undefined

  delete params.date

  const data = await biFetch(url, {
    startDate,
    endDate,
    ...params,
  })
  // console.log(data.data, '------------------------')
  return data
}

const TableList = ({
  onPage,
  tab,
  data: {list = [], pageRet = {}},
  columns = [],
}) => {
  const onShowSizeChange = (current, size) => {
    console.log(current, size)
    pages[tab].pageSize = size
  }

  // console.log('list: ', list)
  // console.log('table:', pages[tab], list)
  const pagination = {
    position: 'bottom',
    current: pages[tab].pageNo,
    total: pageRet.totalCount,
    pageSize: pages[tab].pageSize,
    showTotal: (total) => `共 ${pageRet.totalCount} 条记录`,
    onChange: onPage,
    onShowSizeChange,
    showSizeChanger: true,
    showQuickJumper: true,
  }

  return (
    <Table
      rowKey={() => `tab-${tab}-${nextId()}`}
      dataSource={list}
      columns={columns}
      pagination={pagination}
      scroll={{x: 1500}}
    />
  )
}

const DownloadCSV = ({form, tab}) => {
  const csvInstance = useRef()
  const [data, setData] = useState({})
  const [loading, setLoading] = useState(false)

  const onDownload = async () => {
    setLoading(true)
    let values = form.getFieldsValue()
    const {
      data: {code, data: list},
    } = await fetchList(urls[tab], values)
    if (code === 0) {
      setData(list)
    }
    setLoading(false)
  }

  useEffect(() => {
    if (
      data.list !== undefined &&
      csvInstance.current &&
      csvInstance.current.link
    ) {
      setTimeout(() => {
        csvInstance.current.link.click()
        setData({})
      })
    }
  }, [data])

  let headers = columns[tab].map((column) => ({
    label: column.title,
    key: column.dataIndex,
  }))

  return (
    <>
      <CSVLink
        headers={headers}
        data={data.list || []}
        ref={csvInstance}
      ></CSVLink>
      <Button loading={loading} type="primary" onClick={onDownload}>
        下载
      </Button>
    </>
  )
}

const beforeDate = function (n) {
  var now = new Date()
  now.setDate(now.getDate() - 15)
  return now
}

const afterDate = function (n) {
  var now = new Date()
  now.setDate(now.getDate() + 15)
  return now
}

