import {Layout} from '~/components/Layout'
import React, {useEffect, useState} from 'react'
import apiProduct from '~/api/product'
import {Table, Button, Form, Input, Card, DatePicker, Select} from 'antd'
import moment from 'moment'
import apiAccounting from '~/api/accounting'
const pageParams = {
  pageNum: 1,
  pageSize: 10,
}
const {RangePicker} = DatePicker

const getCurrentMonthFirst = () => {
  var date = new Date()
  date.setDate(1)
  return date
}

const getData = async (values = {}) => {
  const {time} = values
  if (Array.isArray(time)) {
    values = {
      ...values,
      startTime: moment(time[0]).format('YYYY-MM-DD'),
      endTime: moment(time[1]).format('YYYY-MM-DD'),
    }
  } else if (time === undefined) {
    values = {
      ...values,
      startTime: moment(getCurrentMonthFirst(), 'YYYY-MM-DD').format(
        'YYYY-MM-DD',
      ),
      endTime: moment(
        new Date(new Date().setDate(new Date().getDate())),
      ).format('YYYY-MM-DD'),
    }
  } else if (time === null) {
    values = {
      ...values,
      startTime: null,
      endTime: null,
    }
  }
  delete values.time
  try {
    let {
      data: {code, data},
    } = await apiProduct.getMechanism({
      ...pageParams,
      ...values,
    })
    if (code == 0) {
      return data
    }
    return {
      total: 0,
      list: [],
    }
  } catch (e) {
    return {
      total: 0,
      list: [],
    }
  }
}

const breadcrumbs = [
  {text: 'BI报表'},
  {text: '业务台账'},
  {text: '机构考核报表'},
]

function body(props) {
  const [data, setData] = useState(props.data)
  const [form] = Form.useForm()
  const [searchParams, setSearchParams] = useState({})
  const [products, setProducts] = useState([])
  useEffect(() => {
    setProducts(products)
  }, [])
  const onSearch = (value) => {
    pageParams.pageNum = 1
    updateList({...value, ...pageParams})
    setSearchParams({...value, ...pageParams})
  }
  const onResetForm = () => {
    form.resetFields()
    onSearch()
  }

  const onChangePage = (pageNum = pageParams.pageNum) => {
    pageParams.pageNum = pageNum
    updateList({...searchParams, ...pageParams})
  }

  const updateList = async (params) => {
    let data = await getData({...params})
    setData(data)
  }

  const columns = [
    {
      title: '日期',
      dataIndex: 'statDate',
      align: 'left',
      fixed: 'left',
    },
    {
      title: '营销部门',
      dataIndex: 'orgName',
      align: 'left',
      fixed: 'left',
    },
    {
      title: '新增贷款笔数',
      dataIndex: 'loanNum',
      align: 'left',
    },
    {
      title: '新增贷款金额',
      dataIndex: 'loanAmount',
      align: 'left',
    },
    {
      title: '累计贷款笔数',
      dataIndex: 'cumulativeLoanNum',
      align: 'left',
    },
    {
      title: '累计贷款金额',
      dataIndex: 'cumulativeLoanAmount',
      align: 'left',
    },
    {
      title: '新增授信金额',
      dataIndex: 'creditAmount',
      align: 'left',
    },
    {
      title: '累计授信金额',
      dataIndex: 'cumulativeCreditAmoun',
      align: 'left',
    },
    {
      title: '贷款余额',
      dataIndex: 'balance',
      align: 'left',
    },
    {
      title: '用信率',
      dataIndex: 'loanRate',
      align: 'left',
    },
    {
      title: '户均金额',
      dataIndex: 'perHouseholdAmount',
      align: 'left',
    },
    {
      title: '支农比例',
      dataIndex: 'supportAgricultureRatio',
      align: 'left',
    },
    {
      title: '支小比例',
      dataIndex: 'supportSmallRatio',
      align: 'left',
    },
    {
      title: '应收利息',
      dataIndex: 'InterestReceivable',
      align: 'left',
    },
    {
      title: '实收利息',
      dataIndex: 'netReceiptsInterest',
      align: 'left',
    },
    {
      title: '收息率',
      dataIndex: 'interestCollectionRatio',
      align: 'left',
    },
    {
      title: '逾期本金',
      dataIndex: 'overduePrincipal',
      align: 'left',
    },
    {
      title: '逾期利息',
      dataIndex: 'overdueInterest',
      align: 'left',
    },
    {
      title: '逾期率',
      dataIndex: 'overdueRatio',
      align: 'left',
    },
  ]

  const paginationConfig = {
    total: data.total,
    current: pageParams.pageNum,
    showSizeChanger: false,
    showQuickJumper: true,
    defaultPageSize: pageParams.pageSize,
    showTotal: (total) => `共 ${total} 条`,
    onChange: onChangePage,
  }

  const exportExcel = async () => {
    const values = form.getFieldValue()
    let postData = {}
    try {
      if (Array.isArray(values.time)) {
        postData = {
          startTime: moment(values.time[0]).format('YYYY-MM-DD'),
          endTime: moment(values.time[1]).format('YYYY-MM-DD'),
        }
      }
      delete values.time
      const {
        data: {data, code},
      } = await apiAccounting.fetch_mechanism_export({
        ...values,
        ...postData,
      })
      if (code == 0) {
        window.open(data)
      }
    } catch (err) {
      console.log(err)
    }
  }
  return (
    <Layout breadcrumbs={breadcrumbs}>
      <Card style={{marginBottom: 30}}>
        <Form
          form={form}
          name="search"
          onFinish={onSearch}
          layout="inline"
          initialValues={{
            time: [
              moment(getCurrentMonthFirst(), 'YYYY-MM-DD'),
              moment(
                new Date(new Date().setDate(new Date().getDate())),
                'YYYY-MM-DD',
              ),
            ],
            companyId: '',
          }}
        >
          <Form.Item label="营销部门" name="companyId">
            <Select style={{width: 120}}>
              <Select.Option value="">全部</Select.Option>
              {products.map((v) => (
                <Select.Option key={v.orgId} value={v.orgId}>
                  {v.orgName}
                </Select.Option>
              ))}
            </Select>
          </Form.Item>
          <Form.Item label="日期" name="time">
            <RangePicker />
          </Form.Item>
          <Form.Item>
            <Button type="primary" htmlType="submit">
              查询
            </Button>
          </Form.Item>
          <Form.Item>
            <Button type="default" onClick={onResetForm}>
              重置
            </Button>
          </Form.Item>
          <Button
            style={{marginRight: 15}}
            onClick={exportExcel}
            type="primary"
          >
            导出
          </Button>
        </Form>
      </Card>
      <Table
        bordered
        rowKey={(v, i) => i}
        align="center"
        scroll={{x: 'max-content'}}
        pagination={paginationConfig}
        columns={columns}
        dataSource={data.list || []}
      />
    </Layout>
  )
}

body.getInitialProps = async (ctx) => {
  try {
    let data = await getData()
    return {data: data}
  } catch (error) {}
}

export default body
