import {Layout} from '~/components/Layout'
import React, {useEffect, useState} from 'react'
import apiProduct from '~/api/product'
import {Table, Button, Form, Input, Card, DatePicker, Select} from 'antd'
import moment from 'moment'
import {biFetch} from '~/utils/fetch'

const pageParams = {
  pageNum: 1,
  pageSize: 10,
}
const {RangePicker} = DatePicker

const getCurrentMonthFirst = () => {
  var date = new Date()
  date.setDate(1)
  return date
}

const getData = async (values = {}) => {
  const {time} = values
  if (Array.isArray(time)) {
    values = {
      ...values,
      startTime: moment(time[0]).format('YYYY-MM-DD'),
      endTime: moment(time[1]).format('YYYY-MM-DD'),
    }
  } else if (time === undefined) {
    values = {
      ...values,
      startTime: moment(getCurrentMonthFirst(), 'YYYY-MM-DD').format(
        'YYYY-MM-DD',
      ),
      endTime: moment(
        new Date(new Date().setDate(new Date().getDate())),
      ).format('YYYY-MM-DD'),
    }
  } else if (time === null) {
    values = {
      ...values,
      startTime: null,
      endTime: null,
    }
  }
  delete values.time
  try {
    let {
      data: {code, data},
    } = await apiProduct.getLoanSummary({
      ...pageParams,
      ...values,
    })
    if (code == 0) {
      return data
    }
    return {
      total: 0,
      list: [],
    }
  } catch (e) {
    return {
      total: 0,
      list: [],
    }
  }
}

const breadcrumbs = [
  {text: 'BI报表'},
  {text: '业务台账'},
  {text: '产品分类统计报表'},
]

function body(props) {
  const [data, setData] = useState(props.data)
  const [form] = Form.useForm()
  const [searchParams, setSearchParams] = useState({})
  const [products, setProducts] = useState([])

  useEffect(async () => {
    let {
      data: {data, code},
    } = await biFetch('/common/allProduct')
    if (code == 0) {
      setProducts(data)
    }
  }, [])

  const onSearch = (value) => {
    pageParams.pageNum = 1
    updateList({...value, ...pageParams})
    setSearchParams({...value, ...pageParams})
  }
  const onResetForm = () => {
    form.resetFields()
    onSearch()
  }

  const onChangePage = (pageNum = pageParams.pageNum) => {
    pageParams.pageNum = pageNum
    updateList({...searchParams, ...pageParams})
  }

  const updateList = async (params) => {
    let data = await getData({...params})
    setData(data)
  }

  const columns = [
    // {
    //   title: '序号',
    //   dataIndex: 'index',
    //   align: 'left',
    //   fixed: 'left',
    // },
    {
      title: '统计日期',
      dataIndex: 'statDate',
      align: 'left',
      fixed: 'left',
    },
    {
      title: '产品名称',
      dataIndex: 'productName',
      align: 'left',
      fixed: 'left',
    },
    {
      title: '当日新增授信户数',
      dataIndex: 'userNum',
      align: 'left',
    },
    {
      title: '当日新增授信金额',
      dataIndex: 'creditAmount',
      align: 'left',
    },
    {
      title: '当日新增放款笔数',
      dataIndex: 'loanNum',
      align: 'left',
    },
    {
      title: '当日新增放款金额',
      dataIndex: 'loanAmount',
      align: 'left',
    },
    {
      title: '当日新增应收利息',
      dataIndex: 'interestReceivable',
      align: 'left',
    },
    {
      title: '当日新增还款笔数',
      dataIndex: 'repaymentNum',
      align: 'left',
    },
    {
      title: '当日新增还款金额',
      dataIndex: 'repaymentAmount',
      align: 'left',
    },
    {
      title: '当日新增还款本金',
      dataIndex: 'repaymentPrincipal',
      align: 'left',
    },
    {
      title: '当日新增还款利息',
      dataIndex: 'repaymentInterest',
      align: 'left',
    },
    {
      title: '当日新增还款罚息',
      dataIndex: 'repaymentPenaltyInterest',
      align: 'left',
    },
    {
      title: '累计借据笔数',
      dataIndex: 'IOUNum',
      align: 'left',
    },
    {
      title: '累计放款金额',
      dataIndex: 'cumulativeLoanAmount',
      align: 'left',
    },
    {
      title: '累计放款应收利息',
      dataIndex: 'cumulativeLoanInterest',
      align: 'left',
    },
    {
      title: '累计还款笔数',
      dataIndex: 'cumulativeLoanNum',
      align: 'left',
    },
    {
      title: '累计还款金额',
      dataIndex: 'cumulativeReoaymentAmount',
      align: 'left',
    },
    {
      title: '累计已还本金',
      dataIndex: 'cumulativePrincipal',
      align: 'left',
    },
    {
      title: '累计还款利息',
      dataIndex: 'cumulativeInterest',
      align: 'left',
    },
    {
      title: '累计还款罚息',
      dataIndex: 'cumulativePenaltyInterest',
      align: 'left',
    },
    {
      title: '未结清借据笔数',
      dataIndex: '未结清借据笔数',
      align: 'left',
    },
    {
      title: '未结清借据金额',
      dataIndex: 'unclearedIOUAmount',
      align: 'left',
    },
    {
      title: '未结清借据已还本金',
      dataIndex: 'unclearedPrincipal',
      align: 'left',
    },
    {
      title: '未结清借据本金余额',
      dataIndex: 'unclearedBalance',
      align: 'left',
    },
    {
      title: '未结清借据应收利息积数',
      dataIndex: 'unclearedProduct',
      align: 'left',
    },
  ]

  const paginationConfig = {
    total: data.total,
    current: pageParams.pageNum,
    showSizeChanger: false,
    showQuickJumper: true,
    defaultPageSize: pageParams.pageSize,
    showTotal: (total) => `共 ${total} 条`,
    onChange: onChangePage,
  }
  // const disabledDate = (current) => {
  //   return current > new Date()
  // }

  return (
    <Layout breadcrumbs={breadcrumbs}>
      <Card style={{marginBottom: 30}}>
        <Form
          form={form}
          name="search"
          onFinish={onSearch}
          layout="inline"
          initialValues={{
            time: [
              moment(getCurrentMonthFirst(), 'YYYY-MM-DD'),
              moment(
                new Date(new Date().setDate(new Date().getDate())),
                'YYYY-MM-DD',
              ),
            ],
            productId: '',
          }}
        >
          <Form.Item label="产品名称" name="productId">
            <Select style={{width: 120}}>
              <Select.Option value="">全部</Select.Option>
              {products.map((v) => (
                <Select.Option key={v.productId} value={v.productId}>
                  {v.productName}
                </Select.Option>
              ))}
            </Select>
          </Form.Item>
          <Form.Item label="日期" name="time">
            <RangePicker />
          </Form.Item>
          <Form.Item>
            <Button type="primary" htmlType="submit">
              查询
            </Button>
          </Form.Item>
          <Form.Item>
            <Button type="default" onClick={onResetForm}>
              重置
            </Button>
          </Form.Item>
        </Form>
      </Card>
      <Table
        bordered
        rowKey={(v, i) => i}
        align="center"
        scroll={{x: 'max-content'}}
        pagination={paginationConfig}
        columns={columns}
        dataSource={data.list || []}
      />
    </Layout>
  )
}

body.getInitialProps = async (ctx) => {
  let data = await getData()
  return {data: data}
}

export default body
